package javaIO;//CharSteamimport java.io.*;//字符流public class TestChar{	public static void main(String args []){		FileReader fr = null;		FileWriter fw = null;		try{			fr = new FileReader("D:/fromchar.txt");			fw = new FileWriter("D:/tochar.txt");			char [] buffer  = new char[100];			//int temp = fr.read(buffer,0,buffer.length);			//for(int i = 0;i < buffer.length; i++){			//	System.out.println(buffer[i]);			//}			while(true){				int temp = fr.read(buffer,0,buffer.length);				if(temp == -1){					break;				}				fw.write(buffer,0,temp);			}			//fw.write(buffer,0,temp);		}		catch(Exception e){			System.out.println(e);		}		finally{			try{				fr.close();				fw.close();			}			catch(Exception e){				System.out.println(e);			}		}	}}